xwikidoc.content:
= Collection manager script service =
----
{{code}}
#set($cm = $services.collectionManager)
$cm
{{/code}}

Output:

{{info}}
{{velocity}}
#set($cm = $services.collectionManager)
$cm
{{/velocity}}
{{/info}}

== Creating a collection ==
----
{{code}}
#set($collection = $cm.createCollection("Foo"))
$collection
{{/code}}

Output:

{{info}}
{{velocity}}
#set($collection =  $cm.createCollection("TestEncodding"))
$collection
{{/velocity}}
{{/info}}

== Set collection properties ==
----
{{code}}
  $collection.setTitle("Foo")
  $collection.setEmbeddingModel("bert-cpp-minilm-v6")
  $collection.setChunkingMethod('maxChars')
  $collection.setChunkingMaxSize(1000)
  $collection.setChunkingOverlapOffset(24)
  $collection.setDocumentSpaces('AI.Collections.Foo.Documents')
  $collection.setQueryGroups('XWiki.XWikiAdminGroup,XWiki.XWikiAllGroup')
  $collection.setEditGroups('XWiki.XWikiAdminGroup')
  $collection.setAdminGroups('XWiki.XWikiAdminGroup')
  $collection.setRightsCheckMethod('rightsCheckJWT')
  $collection.setRightsCheckMethodParam('https://testURL.com')
{{/code}}

Output:

{{info}}
{{velocity}}
  $collection.setTitle("Ttestencoding")
  $collection.setEmbeddingModel("AI.Models.bert")
  $collection.setChunkingMethod('maxChars')
  $collection.setChunkingMaxSize(1000)
  $collection.setChunkingOverlapOffset(24)
  $collection.setDocumentSpaces('AI.Collections.Foo.Documents')
  $collection.setQueryGroups('XWiki.XWikiAdminGroup,XWiki.XWikiAllGroup')
  $collection.setEditGroups('XWiki.XWikiAdminGroup')
  $collection.setAdminGroups('XWiki.XWikiAdminGroup')
  $collection.setRightsCheckMethod('rightsCheckJWT')
  $collection.setRightsCheckMethodParam('https://testURL.com')
{{/velocity}}
{{/info}}

== Save the collection ==
----
{{code}}
$collection.save()
{{/code}}

Output:

{{info}}
{{velocity}}
$collection.save()
{{/velocity}}
{{/info}}

