==== Create a new document ====
----
{{code}}
#set($barDoc = $collection.newDocument('Bar'))
$barDoc
{{/code}}

Output:

{{info}}
{{velocity}}
#set($barDoc = $collection.newDocument('test'))
$barDoc
{{/velocity}}
{{/info}}

==== Set document properties ====
----
{{code}}
$barDoc.setTitle('Alice in wonderland')
$barDoc.setLanguage('en')
$barDoc.setURL($barDoc.getXWikiDocument().getURL('view', $xcontext.getContext()))
$barDoc.setCollection('Foo')
$barDoc.setMimetype('plain/text')
$barDoc.setContent('Test Content')
{{/code}}

Output:

{{info}}
{{velocity}}
$barDoc.setTitle('Test')
$barDoc.setLanguage('en')
$barDoc.setURL($barDoc.getXWikiDocument().getExternalURL('view', $xcontext.getContext()))
$barDoc.setCollection('TestEncodding')
$barDoc.setMimetype('plain/text')
$barDoc.setContent('Test Content with more than 24 characters or else the embeddings will fail for this.')
{{/velocity}}
{{/info}}

==== Save the document ====
----
{{code}}
$barDoc.save()
{{/code}}

Output:

{{info}}
{{velocity}}
$barDoc.save()
{{/velocity}}
{{/info}}

